import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import GameBoard from "./GameBoard";
import GameTimer from "./Gametimer";
import "./GamePage.css";

function GamePage() {
  const navigate = useNavigate();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const level = queryParams.get("level") || "medium";

  // ðŸ‡ Controls the mole speed only
  const getSpeed = () => {
    switch (level) {
      case "easy": return 1200;  // slower
      case "hard": return 500;   // faster
      default: return 800;       // medium
    }
  };

  const moleCount = 9; // ðŸ•³ï¸ Keep fixed number of holes

  const [paused, setPaused] = useState(true);
  const [resetKey, setResetKey] = useState(0);
  const [moles, setMoles] = useState(Array(moleCount).fill(false));
  const [score, setScore] = useState(0);

  useEffect(() => {
    if (!paused) {
      const interval = setInterval(() => {
        const newMoles = Array(moleCount).fill(false);
        const index = Math.floor(Math.random() * moleCount);
        newMoles[index] = true;
        setMoles(newMoles);
      }, getSpeed()); // â±ï¸ Use speed here
      return () => clearInterval(interval);
    } else {
      setMoles(Array(moleCount).fill(false));
    }
  }, [paused, resetKey, level]); // add `level` so speed changes on difficulty

  const handleWhack = (index) => {
    if (moles[index]) {
      setScore(score + 1);
      const updated = [...moles];
      updated[index] = false;
      setMoles(updated);
    }
  };

  const handleTimeUp = () => {
    setPaused(true);
    localStorage.setItem('highScore', Math.max(score, getHighScore()));
    alert(`Time is up! Your score: ${score}`);
  };

  const getHighScore = () => parseInt(localStorage.getItem('highScore')) || 0;

  return (
    <div className="game-container">
      <h2>Level: {level}</h2>
      <h2>Score: {score}</h2>
      <GameTimer
        startTime={30}
        paused={paused}
        onTimeUp={handleTimeUp}
        resetTrigger={resetKey}
      />

      <GameBoard moles={moles} handleWhack={handleWhack} />

      <div className="game-controls">
        <button onClick={() => setPaused(!paused)}>{paused ? "Resume" : "Pause"}</button>
        <button onClick={() => {
          setResetKey(resetKey + 1);
          setScore(0);
          setPaused(false);
        }}>Restart</button>
        <button onClick={() => navigate("/")}>Home</button>
      </div>
    </div>
  );
}

export default GamePage;
