import React, { useState, useEffect } from 'react';
import './GamePage.css';
import moleImg from '../public/CuteMole.png'; // adjust path if needed
import hammerCursor from '../public/hammer.png'; // for cursor if you want

function GamePage() {
  const [holes, setHoles] = useState(Array(9).fill(false)); // mole visible or not in each hole

  // Show a mole randomly every 1 sec
  useEffect(() => {
    const interval = setInterval(() => {
      const newHoles = Array(9).fill(false);
      const randomIndex = Math.floor(Math.random() * 9);
      newHoles[randomIndex] = true;
      setHoles(newHoles);
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Handler when mole is whacked (clicked)
  const handleWhack = (index) => {
    if (holes[index]) {
      alert('Whacked mole at hole #' + (index + 1));
      // Hide mole after whack
      const newHoles = [...holes];
      newHoles[index] = false;
      setHoles(newHoles);
    }
  };

  return (
    <div className="game-container">
      <h1 className="game-title">WHACK-A-MOLE</h1>
      <div
        className="grid"
        style={{ cursor: `url(${hammerCursor}), auto` }}
      >
        {holes.map((isVisible, index) => (
          <div key={index} className="hole" onClick={() => handleWhack(index)}>
            <img
              src={moleImg}
              alt="Mole"
              className={`mole ${isVisible ? 'up' : ''}`}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default GamePage;
