import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import GameBoard from "./GameBoard";
import GameTimer from "./Gametimer";
import "./GamePage.css";

function GamePage() {
  const navigate = useNavigate();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const level = queryParams.get("level") || "medium";

  const getSpeed = () => {
    switch (level) {
      case "easy": return 1200;
      case "hard": return 500;
      default: return 800;
    }
  };

  const moleCount = 9;

  const [paused, setPaused] = useState(true);
  const [showMenu, setShowMenu] = useState(false); // ðŸ‘ˆ New state for pause menu
  const [resetKey, setResetKey] = useState(0);
  const [moles, setMoles] = useState(Array(moleCount).fill(false));
  const [score, setScore] = useState(0);

  useEffect(() => {
    if (!paused) {
      const interval = setInterval(() => {
        const newMoles = Array(moleCount).fill(false);
        const index = Math.floor(Math.random() * moleCount);
        newMoles[index] = true;
        setMoles(newMoles);
      }, getSpeed());
      return () => clearInterval(interval);
    } else {
      setMoles(Array(moleCount).fill(false));
    }
  }, [paused, resetKey, level]);

  const handleWhack = (index) => {
    if (moles[index]) {
      setScore(score + 1);
      const updated = [...moles];
      updated[index] = false;
      setMoles(updated);
    }
  };

