import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import GameBoard from "./GameBoard";
import GameTimer from "./Gametimer";
import "./GamePage.css";

function GamePage() {
  const navigate = useNavigate();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const level = queryParams.get("level") || "medium";

  const getMoleCount = () => {
    switch (level) {
      case "easy": return 1200;
      case "hard": return 12;
      default: return 9;
    }
  };

  const [paused, setPaused] = useState(true);
  const [resetKey, setResetKey] = useState(0);
  const [moles, setMoles] = useState([]);
  const [score, setScore] = useState(0);

  const moleCount = getMoleCount();

  useEffect(() => {
    setMoles(Array(moleCount).fill(false));
  }, [moleCount]);

  useEffect(() => {
    if (!paused) {
      const interval = setInterval(() => {
        const newMoles = Array(moleCount).fill(false);
        const index = Math.floor(Math.random() * moleCount);
        newMoles[index] = true;
        setMoles(newMoles);
      }, 1000);
      return () => clearInterval(interval);
    } else {
      setMoles(Array(moleCount).fill(false));
    }
  }, [paused, resetKey]);

  const handleWhack = (index) => {
    if (moles[index]) {
      setScore(score + 1);
      const updated = [...moles];
      updated[index] = false;
      setMoles(updated);
    }
  };

  const handleTimeUp = () => {
    setPaused(true);
    localStorage.setItem('highScore', Math.max(score, getHighScore()));
    alert(`Time is up! Your score: ${score}`);
  };

  const getHighScore = () => parseInt(localStorage.getItem('highScore')) || 0;

  return (
    <div className="game-container">
      <h2>Level: {level}</h2>
      <h2>Score: {score}</h2>
      <GameTimer startTime={30} paused={paused} onTimeUp={handleTimeUp} resetTrigger={resetKey} />

      <GameBoard moles={moles} handleWhack={handleWhack} />

      <div className="game-controls">
        <button onClick={() => setPaused(!paused)}>{paused ? "Resume" : "Pause"}</button>
        <button onClick={() => {
          setResetKey(resetKey + 1);
          setScore(0);
          setPaused(false);
        }}>Restart</button>
        <button onClick={() => navigate("/")}>Home</button>
      </div>
    </div>
  );
}

export default GamePage;
